import React from 'react';
import { Link } from 'react-router-dom';
import { AppBar, Avatar, Button, Typography } from '@material-ui/core';

import useStyles from './styles';
import flashback from '../../images/flashback.png';

const Navbar = () => {
    const classes = useStyles();

    const user = null;

    return (
        <AppBar className = {classes.appBar}position="static" color="inherit">
            <div className={classes.brandContainer}>
                <Typography className ={classes.heading}variant="h3" align="center">FlashBack</Typography>
                <img className ={classes.image}src={flashback} alt="flashback" height="50"/>
            </div>
            <Toolbar className={classes.toolbar}>
                {user ? (
                    <div className={classes.profile}>
                        <Avatar className={classes.purple} alt={user.result.name} src={user.result.imageUrl}>{user.result.name.charAt(0)}</Avatar>
                        <Typography className={classes.username} variant="h6">{user.result.name}</Typography>
                        <Button variant="contained" className={classes.logout} color="secondary">Logout</Button>
                    </div>
                ) : (
                    <Button component={Link}> </Button>
                )}
            </Toolbar>
        </AppBar>
    );
};

export default Navbar;
