export default (posts = [], action) => {
    console.log('payload on reducer:: ',action.payload)
    switch (action.type) {
        case 'UPDATE':
            return posts.map((post) => post._id === action.paylad) 
        case 'FETCH_ALL':
            return action.payload;
        case 'CREATE':
            return [...posts, action.payload];
        default:
            return posts;
    }
}
